using AutoMapper;using Microsoft.AspNetCore.Mvc;using ProgramFormCore.Models;
using ProgramFormInfrastructure.DTOs.Request;using ProgramFormInfrastructure.DTOs.Response;
using ProgramFormInfrastructure.Interfaces;namespace ProgramForm.Controllers{    [Route("api/[controller]")]    [ApiController]    public class ApplicationFormController : ControllerBase    {        private readonly IApplicationFormService _appFormService;        private readonly IMapper _mapper;        public ApplicationFormController(IApplicationFormService appFormService, IMapper mapper)        {            _appFormService = appFormService;            _mapper = mapper;        }        [HttpGet]        [ProducesResponseType(StatusCodes.Status200OK, Type = typeof(IEnumerable<ApplicationFormResponseDto>))]
        public async Task<ActionResult<IEnumerable<ApplicationFormResponseDto>>> Get()        {            var appForms = await _appFormService.GetAllApplicationAsync();
            var appFormsDto = _mapper.Map<IEnumerable<ApplicationFormResponseDto>>(appForms);            return Ok(appFormsDto);        }        [HttpPut("{id}")]        [ProducesResponseType(StatusCodes.Status400BadRequest)]
        [ProducesResponseType(StatusCodes.Status404NotFound)]
        [ProducesResponseType(StatusCodes.Status200OK, Type = typeof(ApplicationFormDto))]        public async Task<ActionResult<ApplicationFormDto>> UpdateApplicationForm(string id, [FromBody] ApplicationFormDto appFormDto)        {            if (appFormDto == null)            {                return BadRequest("ProgramDetails object is null");            }            var existingApplicationForm = await _appFormService.GetApplicationFormByIdAsync(id);            if (existingApplicationForm == null)            {                return NotFound();            }
            _mapper.Map(appFormDto, existingApplicationForm);            var updatedForm = await _appFormService.UpdateApplicationAsync(id, existingApplicationForm);            var updatedProgramDetailsDto = _mapper.Map<ApplicationForm, ApplicationFormDto>(updatedForm);
            return Ok(updatedProgramDetailsDto);        }    }}